mapper.select:
SELECT distinct m.* ,i.* FROM gs_article_lang_main m LEFT JOIN gs_article_lang_info i ON m.article_id = i.article_id where 1=1
and i.article_title like CONCAT('%',#{articleTitle},'%')
and i.article_author like CONCAT('%',#{articleTitle},'%')
and m.article_state = #{articleState}
and m.article_class_id = #{articleClassId}
order by m.article_sort,m.article_publish_time desc

mapper.update:
update gs_article_lang_main
set article_sort = #{articleSort,jdbcType=BIT}
where article_id = #{articleId,jdbcType=BIGINT}

mapper.select:
SELECT
m.* ,i.*
FROM
gs_article_lang_main m
LEFT JOIN gs_article_lang_info i ON m.article_id = i.article_id
where m.article_state=3

  and year(m.article_publish_time)= #{year}


  and  m.article_class_id = #{articleClassId,jdbcType=BIGINT}

and i.lang_type=#{langType}
order by m.article_sort,m.article_publish_time desc

SELECT m.article_id , m.article_class_id,m.article_image,m.article_start_time,m.article_end_time, m.article_publish_time,m.article_sort,m.article_state,m.article_publisher_name,m.article_publisher_id, i.article_info_id,i.article_title,i.article_author,i.article_abstract FROM gs_article_lang_main m LEFT JOIN gs_article_lang_info i ON m.article_id = i.article_id where m.article_state=3
and year(m.article_publish_time)= #{year}
and m.article_class_id = #{articleClassId,jdbcType=BIGINT}
and i.lang_type=#{langType} order by m.article_sort,m.article_publish_time desc

SELECT m.article_id , m.article_class_id,m.article_image,m.article_start_time,m.article_end_time, m.article_publish_time,m.article_sort,m.article_state,m.article_publisher_name,m.article_publisher_id, i.article_info_id,i.article_title,i.article_author,i.article_abstract FROM gs_article_lang_main m LEFT JOIN gs_article_lang_info i ON m.article_id = i.article_id where m.article_state=3
and year(m.article_publish_time)= #{year}
and m.article_class_id = #{articleClassId,jdbcType=BIGINT}
and i.lang_type=#{langType} and m.article_image is not null and m.article_image !='' order by m.article_sort,m.article_publish_time desc
